files.content:
body { font-family: sans-serif; margin: 0; padding: 0; }
header { background-color: #f0f0f0; padding: 1em; text-align: center; }
main { padding: 1em; }
.hero { text-align: center; margin-bottom: 2em; }
.hero img { width: 100%; max-width: 600px; }
#projects { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 1em; }
#projects article { border: 1px solid #ccc; padding: 1em; }
#projects img { width: 100%; }
footer { text-align: center; padding: 1em; background-color: #f0f0f0; }

/* Mobile-first adjustments */
@media (min-width: 768px) {
  .hero img { max-width: 800px; }
}

/* Accent color */
a { color: #22D3EE; }
