files.content:
body { font-family: sans-serif; margin: 0; padding: 0; }
header { background-color: #f0f0f0; padding: 1em; text-align: center; }
main { padding: 1em; }
.hero { text-align: center; margin-bottom: 2em; }
.hero img { max-width: 100%; height: auto; }
#projects { display: flex; flex-wrap: wrap; justify-content: space-around; }
#projects article { width: 30%; margin-bottom: 1em; border: 1px solid #ccc; padding: 0.5em; box-sizing: border-box; }
#projects article img { max-width: 100%; height: auto; }

@media (max-width: 600px) {
  #projects article { width: 100%; }
}
